// all videos listed and add-to button
import axios from 'axios';
import React, { Component } from 'react';
import { Link } from "react-router-dom";


class JavaScripts extends Component {
    state = {
        jsVideos: [],
        newJsVideo: {
            name: '',
            description: ''
        },
        isVideoFormDisplayed: false
    }

    componentDidMount = () => {
      axios.get('/api/JavaScript').then(res => {
          this.setState({jsVideos: res.data})
      })
    }

    toggleVideoForm = () => {
        this.setState((state, props) => {
            return ({isVideoFormDisplayed: !state.isVideoFormDisplayed})
        })
    }

    handleChange = (e) => {
      const cloneNewVideo = {...this.state.newJsVideo}
      cloneNewVideo[e.target.name] = e.target.value
      this.setState({newJsVideo: cloneNewVideo})
    }

    createVideo = (e) => {
      e.preventDefault()
      axios
          .post('/api/JavaScript', {
              name: this.state.newVideo.name,
              description: this.state.newVideo.description
          })
          .then(res => {
              const videosList = [...this.state.videos]
              videosList.unshift(res.data)
              this.setState({
                  newJsVideo: {
                      name: '',
                      description: '',
                      link: '',
                  },
                  isVideoFormDisplayed: false,
                  jsVideos: videosList
              })
          })

    }

    render() {
        const videoListElement = this.state.jsVideos.map((jsVideo, i) => {
            const link = '/${jsVideo.id}'
            return ( <div>
                <a href={link} key={i}>{jsVideo.name} </a>
                </div>)
        })
      return (
        <div className="video-container" >
          <h1>JavaScript Videos</h1>
          {this.state.newJsVideo ? 
          <button onClick={this.toggleVideoForm}> New Video</button>
          :
          <div className="create-form">
            <button onClick={this.toggleVideoForm}>Hide</button>
            <form onSubmit={this.createVideo}>
                <input type="text" name="newJsVideoName" placeholder="Title" onChange={this.onVideoFormChange}/>

            </form>
            </div>
          }
        </div>
        )


        }
    }


  export default JavaScripts
