// all videos listed and add-to button
import axios from 'axios';
import React, { Component } from 'react';
import { Link } from "react-router-dom";


class JavaScripts extends Component {
    state = {
        jsVideos: [],
        newJsVideo: {
            name: '',
            description: ''
        },
        isVideoFormDisplayed: false
    }

    componentDidMount = () => {
      axios.get('/api/JavaScript').then(res => {
          this.setState({jsVideos: res.data})
      })
    }

    toggleVideoForm = () => {
        this.setState((state, props) => {
            return ({isVideoFormDisplayed: !state.isVideoFormDisplayed})
        })
    }

    handleChange = (e) => {
      const cloneNewVideo = {...this.state.newJsVideo}
      cloneNewVideo[e.target.name] = e.target.value
      this.setState({newJsVideo: cloneNewVideo})
    }

    createVideo = (e) => {
      e.preventDefault()
      axios
          .post('/api/JavaScript', {
              name: this.state.newVideo.name,
              description: this.state.newVideo.description
          })
          .then(res => {
              const videosList = [...this.state.videos]
              videosList.unshift(res.data)
              this.setState({
                  newJsVideo: {
                      name: '',
                      description: '',
                      link: '',
                  },
                  isVideoFormDisplayed: false,
                  jsVideos: videosList
              })
          })

    }

    render() {

      return (
        <div>
          <h1>JavaScript Videos</h1>
          {
              this.state.videos.map(video => {
                  return (
                      <div key={video._id}>
                          <Link
                              to={`/${video._id}`}
                          >
                              {video.name}
                          </Link>
                      </div>
                  )
              })
          }
          <button onClick={this.toggleVideoForm}> New Video</button>
          {
              this.state.isVideoFormDisplayed
                  ? <form onSubmit={this.createVideo}>
                      <div>
                          <label htmlFor="name">Name</label>
                          <input
                              id="name"
                              type="text"
                              name="name"
                              onChange={this.handleChange}
                              value={this.state.newCreature.name}
                          />
                      </div>
                      <div>
                          <label htmlFor="description">Description</label>
                          <textarea
                              id="description"
                              type="text"
                              name="description"
                              onChange={this.handleChange}
                              value={this.state.newCreature.description}
                          />
                      </div>
                      <button>Create</button>
                  </form>
                  : null
          }
        </div>
      )
    }
  }

  export default JavaScripts
